# Combien y a-t-il de journalistes dans ce array ?

def how_many_journalists(handles_twitter)
    return handles_twitter.count
end

# how_many_journalists(handles_twitter)

# Combien d'handle contiennent un numéro ?

def how_many_journalists_with_number(handles_twitter)
    counter = 0
    handles_twitter.each {|handle| counter += 1 if handle =~ /\d/}
    return counter
end

# how_many_journalists_with_number(handles_twitter)

# Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?

def how_many_journalists_with_Aude?(handles_twitter)
    counter = 0
    handles_twitter.each {|handle| counter += 1 if handle.downcase =~ /(aude)/}
    return counter
end

# how_many_journalists_with_Aude?(handles_twitter)

# Combien commencent par une majuscule (première lettre juste après le @) ?

def first_upper?(handles_twitter)
    counter = 0
    handles_twitter.each {|handle| counter += 1 if handle[1] =~ /[A-Z]/ }
    return counter
end

# first_upper?(handles_twitter)

# Combien contiennent au moins une majuscule ?

def how_many_with_capital(handles_twitter)
    counter = 0
    handles_twitter.each { |handle| counter += 1 if handle =~ /[A-Z]/ }
    return counter
end

# how_many_with_capital(handles_twitter)

# Combien y a-t-il de underscore _ dans tous les pseudos confondus ?

def how_many_underscore(handles_twitter)
    counter = 0
    handles_twitter.each {|handle| counter += handle.count("_")}
    return counter
end

# how_many_underscore(handles_twitter)

# Trie la liste de handle par ordre alphabétique.

def alphabetic_sort(handles_twitter)
    return handles_twitter.sort_by {|handle| handle.downcase } 
end

# alphabetic_sort(handles_twitter)

# Quels sont les 50 handles les plus courts de ce array ?

def fifty_shortest(handles_twitter)
    return handles_twitter.sort_by{|s| s.length }[0...50]
end

# fifty_shortest(handles_twitter)

# Quelle est la position dans l'array de la personne @epenser ?

def find_index_of_element(handles_twitter)
    return handles_twitter.find_index("@epenser")
end

# find_index_of_element(handles_twitter)

def menu(handles_twitter)
    menu = true

    puts "\n\n"
    puts "=" * 35 + " Jouons avec nos journalistes " +  "=" * 35 + "\n\n"
    puts "1) Combien de journalistes de BFM TV ?"
    puts "2) Combien de journalistes on un nombre dans leur pseudo ?"
    puts "3) Combien de journalistes on 'Aude' dans leur pseudo ?"
    puts "4) Combien de journalistes ont un pseudo qui commence par une lettre majuscule ?"
    puts "5) Combien de journalistes ont un pseudo qui contient une lettre majuscule ?"
    puts "6) Combien d'underscores dans tous les pseudos des journalistes ?"
    puts "7) Trier les journalistes par ordre alphabétique."
    puts "8) Afficher les 50 pseudos de journalistes les plus court."
    puts "9) Quel est le numéro d'index de @epenser ?"
    puts "\n"
    puts "10) Quitter"
    print "> "

    choise_number = gets.chomp
   
    case choise_number
    when '1'
        puts "\n\n"
        puts "Il y a #{how_many_journalists(handles_twitter)} sur BFM TV."
        print "\n[Enter] pour continuer"
        gets.chomp
    when '2'
        puts "\n\n"
        puts "Il y a #{how_many_journalists_with_number(handles_twitter)} pseudos avec un numéro."
        print "\n[Enter] pour continuer"
        gets.chomp
    when '3'
        puts "\n\n"
        puts "Il y a #{how_many_journalists_with_Aude?(handles_twitter)} pseudos inclant \"Aude\"."
        print "\n[Enter] pour continuer"
        gets.chomp
    when '4'
        puts "\n\n"
        puts "Il y a #{first_upper?(handles_twitter)} pseudos commencent par une lettre majuscule."
        print "\n[Enter] pour continuer"
        gets.chomp
    when '5'
        puts "\n\n"
        puts "Il y a #{how_many_with_capital(handles_twitter)} pseudos qui contiennent une lettre majuscule."
        print "\n[Enter] pour continuer"
        gets.chomp
    when '6'
        puts "\n\n"
        puts "Il y a #{how_many_underscore(handles_twitter)} underscore dans les pseudos."
        print "\n[Enter] pour continuer"
        gets.chomp
    when '7'
        puts "\n\n"
        puts "Pseudo par ordre alphabétique : "
        puts alphabetic_sort(handles_twitter)
        print "\n[Enter] pour continuer"
        gets.chomp
    when '8'
        puts "\n\n"
        puts "Liste des 50 pseudos les plus court : "
        puts fifty_shortest(handles_twitter)
        print "\n[Enter] pour continuer"
        gets.chomp
    when '9'
        puts "\n\n"
        print "Le pseudo @epenser est en position: "
        puts find_index_of_element(handles_twitter)
        print "\n[Enter] pour continuer"
        gets.chomp
    when '10'
        menu = false
        puts "\n"
        puts "=" * 35 + " A bientot ! " +  "=" * 35 + "\n\n"
    end

    menu(handles_twitter) if menu
    
end

menu(handles_twitter)